(function(){
    
    const toTop = document.querySelectorAll('a[href*="#top"]'); 
    const toDown = document.querySelectorAll('a[href*="#down"]'); 
    const downItem = document.getElementById('down') 

    for(let i = 0; i<toTop.length; i++ ) { 
        toTop[i].addEventListener("click", function(event) { 
            event.preventDefault(); 
            window.scrollTo({ 
                top: 0,
                behavior: "smooth"
            })
        })
    }
    for(let i = 0; i<toDown.length; i++ ) { 
        toDown[i].addEventListener("click", function(event) { 
            event.preventDefault(); 
            window.scrollTo({ 
                top: downItem.offsetTop - 200,
                behavior: "smooth"
            })
        })
    }
   
 
}());
 
