// header sticty function
$(window).scroll(function() {
    if ($(this).scrollTop() > 1){  
        $('header').addClass("sticky");
    }
    else{
        $('header').removeClass("sticky");
    }
});

// set city function 
$('#yourcity').change( setCity )
 
function setCity() { 
     let value = $('#yourcity option[selected]')[0].value; 
     let url_one = 'assets/img/screen-one/'
     let url_two = 'assets/img/screen-three/'

     $('.js_screen_one_bg').css('background-image' , 'url(' + url_one + value + '.jpg' + ')' ) 
     $('.js_screen_two_bg').css('background-image' , 'url(' + url_two + value + '.jpg' + ')' ) 
}
$(window).on('load', setCity)